import rclpy
from rclpy.node import Node
from std_msgs.msg import Bool
from geometry_msgs.msg import Twist
from std_srvs.srv import Trigger
import cv2
import numpy as np
import time


class TableAlignNode(Node):
    def __init__(self):
        super().__init__('table_align_node')

        self.alignment_pub = self.create_publisher(Bool, '/table_alignment_done', 10)
        self.cmd_vel_pub = self.create_publisher(Twist, '/cmd_vel', 10)
        self.align_service = self.create_service(Trigger, 'table_align_service', self.handle_align_request)

        self.cap = cv2.VideoCapture(0)
        if not self.cap.isOpened():
            self.get_logger().error('❌ 카메라 열기 실패')
            exit()

        self.tolerance = 40
        self.move_speed = 0.05
        self.required_duration = 0.5

        self.get_logger().info('📷 TableAlignNode with alignment criteria started.')

    def handle_align_request(self, request, response):
        self.get_logger().info('📥 정렬 요청 수신. 영상 기반 정렬 시작.')

        aligned = False
        satisfy_start_time = None

        while rclpy.ok():
            ret, frame = self.cap.read()
            if not ret:
                self.get_logger().warn('⚠️ 프레임 읽기 실패')
                continue

            output = frame.copy()
            h, w = frame.shape[:2]
            image_cx = w // 2
            twist = Twist()

            # 전처리
            gray = cv2.cvtColor(frame, cv2.COLOR_BGR2GRAY)
            blurred = cv2.GaussianBlur(gray, (15, 15), 0)
            edges = cv2.Canny(blurred, 15, 80)

            # 허프 라인 검출
            lines = cv2.HoughLinesP(edges, 0.2, np.pi / 520, threshold=30, minLineLength=100, maxLineGap=40)

            best_line = None
            max_length = 0

            if lines is not None:
                for line in lines:
                    x1, y1, x2, y2 = line[0]
                    dx = x2 - x1
                    dy = y2 - y1
                    length = np.hypot(dx, dy)
                    angle = abs(np.arctan2(dy, dx) * 180 / np.pi)

                    if length >= (w * 0.3) and angle <= 20:
                        if length > max_length:
                            best_line = (x1, y1, x2, y2)
                            max_length = length

            found = False
            delta_x = 0
            table_cx = None

            if best_line is not None:
                x1, y1, x2, y2 = best_line
                table_cx = (x1 + x2) // 2
                delta_x = table_cx - image_cx
                found = True

                # 시각화
                cv2.line(output, (x1, y1), (x2, y2), (0, 0, 255), 3)
                cv2.line(output, (image_cx, 0), (image_cx, h), (255, 0, 0), 2)
                cv2.line(output, (table_cx, 0), (table_cx, h), (0, 255, 0), 2)
                cv2.putText(output, f'delta_x: {delta_x}', (10, 30),
                            cv2.FONT_HERSHEY_SIMPLEX, 0.9, (0, 0, 0), 2)

            # 정렬 판단 및 이동 명령
            if found:
                self.get_logger().info(f'📐 delta_x = {delta_x}')
                if abs(delta_x) < self.tolerance:
                    if satisfy_start_time is None:
                        satisfy_start_time = time.time()
                    elif time.time() - satisfy_start_time >= self.required_duration:
                        aligned = True
                        break
                    twist.linear.x = 0.0
                else:
                    satisfy_start_time = None
                    twist.linear.x = self.move_speed if delta_x < 0 else -self.move_speed
            else:
                satisfy_start_time = None
                twist.linear.x = 0.0

            self.cmd_vel_pub.publish(twist)

            cv2.imshow("Hough Line Detection", output)
            if cv2.waitKey(10) & 0xFF == ord('q'):
                self.get_logger().info('🛑 사용자가 종료했습니다.')
                break

        self.cmd_vel_pub.publish(Twist())

        if aligned:
            self.alignment_pub.publish(Bool(data=True))
            response.success = True
            response.message = '정렬 성공'
            self.get_logger().info('✅ 정렬 완료. /table_alignment_done 발행')
        else:
            response.success = False
            response.message = '정렬 실패 또는 종료됨'

        return response

    def destroy_node(self):
        self.cap.release()
        cv2.destroyAllWindows()
        super().destroy_node()


def main(args=None):
    rclpy.init(args=args)
    node = TableAlignNode()
    try:
        rclpy.spin(node)
    except KeyboardInterrupt:
        node.get_logger().info('🛑 사용자 종료')
    finally:
        node.destroy_node()
        rclpy.shutdown()
